import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { ScaleLoader } from "react-spinners";
import './style.css';

export const HalakList = () => {
    const [halak, setHalak] = useState([]);
    const [isPending, setPending] = useState(false);

    useEffect(() => {
        setPending(true);
        axios.get('https://localhost:7067/api/Halak', { responseType: 'arraybuffer' }) // BLOB képek lekérése
        .then(valasz => {
            const halakWithImages = valasz.data.map(hal => {
                // Átalakítjuk a BLOB adatokat base64 formátumra
                const base64Image = Buffer.from(hal.kep, 'binary').toString('base64');
                return { ...hal, kep: `data:image/png;base64,${base64Image}` }; // PNG esetén
            });
            setHalak(halakWithImages);
        })
        .catch(hiba => console.log(hiba))
        .finally(() => setPending(false));
    }, []);

    return (
        <div className="container">
            {isPending ? (
                <ScaleLoader color="#007BFF" size={50} />
            ) : (
                <div className="grid-container">
                    {halak.map((hal, index) => (
                        <div className="card" key={index}>
                            <img src={hal.kep} alt={hal.nev} className="image" />
                            <h3 className="card-title">{hal.faj}</h3>
                            <p className="card-description">Méret: {hal.meretCm} cm</p>
                            <Link to={`/hal/${hal.id}`} className="btn btn-primary">Részletek</Link>
                            <Link to={`/mod-hal/${hal.id}`} className='btn btn-warning'>
                                <i className="bi bi-pencil-square fs-3"></i>
                            </Link>
                            &nbsp;&nbsp;
                            <Link to={`/del-hal/${hal.id}`} className='btn btn-danger'>
                                <i className="bi bi-trash2 fs-3"></i>
                            </Link>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
