import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { ScaleLoader } from "react-spinners";
import "./style.css";

export const HalakList = () => {
    const [halak, setHalak] = useState([]);
    const [isPending, setPending] = useState(false);

    useEffect(() => {
        fetchHalak();
    }, []);

    const fetchHalak = () => {
        setPending(true);
        axios.get("https://localhost:7067/api/Halak")
            .then(response => {
                if (Array.isArray(response.data)) {
                    const halakWithImages = response.data.map(hal => ({
                        ...hal,
                        kep: hal.kep ? `data:image/png;base64,${hal.kep}` : null,
                    }));
                    setHalak(halakWithImages);
                } else {
                    console.error("Hib√°s v√°laszform√°tum:", response.data);
                }
            })
            .catch(error => console.error("Hiba t√∂rt√©nt:", error))
            .finally(() => setPending(false));
    };

    const deleteHal = (id) => {
        if (window.confirm("Biztosan t√∂rl√∂d ezt a halat?")) {
            axios.delete(`https://localhost:7067/api/Halak/${id}`)
                .then(() => {
                    setHalak(halak.filter(hal => hal.id !== id));
                })
                .catch(error => console.error("Hiba t√∂rt√©nt a t√∂rl√©sn√©l:", error));
        }
    };

    return (
        <div className="container">
            {isPending ? (
                <div className="loading">
                    <ScaleLoader color="#007BFF" size={50} />
                </div>
            ) : (
                <div className="grid-container">
                    {halak.map((hal) => (
                        <div className="card" key={hal.id}>
                            {hal.kep ? (
                                <img src={hal.kep} alt={hal.nev} className="fish-image" />
                            ) : (
                                <p className="no-image">Nincs k√©p</p>
                            )}
                            <h3 className="fish-title">{hal.faj}</h3>
                            <p className="fish-size">M√©ret: {hal.meretCm} cm</p>
                            <div className="button-group">
                                <Link to={`/halak/${hal.id}`} className="details-button">R√©szletek</Link>
                                <Link to={`/mod-hal/${hal.id}`} className="edit-button">‚úèÔ∏è</Link>
                                <button onClick={() => deleteHal(hal.id)} className="delete-button">üóëÔ∏è</button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
