// src/components/HalDetails.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HalDetails = ({ match }) => {
  const [hal, setHal] = useState(null);

  useEffect(() => {
    axios.get(`/api/halak/${match.params.id}`)
      .then(response => setHal(response.data))
      .catch(error => console.error(error));
  }, [match.params.id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHal({ ...hal, [name]: value });
  };

  const handleSave = () => {
    axios.put(`/api/halak/${hal.id}`, hal)
      .then(response => alert('Saved!'))
      .catch(error => console.error(error));
  };

  if (!hal) return <div>Loading...</div>;

  return (
    <div>
      <h1>{hal.name}</h1>
      <img src={`data:image/jpeg;base64,${hal.image}`} alt={hal.name} />
      <input type="text" name="name" value={hal.name} onChange={handleChange} />
      <button onClick={handleSave}>Save</button>
      <button onClick={() => axios.delete(`/api/halak/${hal.id}`).then(() => window.location.href = '/')}>Delete</button>
    </div>
  );
};

export default HalDetails;
