import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { ScaleLoader } from "react-spinners";
import './style.css';

export const HalakList = () => {
    const [halak, setHalak] = useState([]);
    const [isPending, setPending] = useState(false);

    useEffect(() => {
        setPending(true);
        axios.get('https://localhost:5001/api/Halak')
        .then(valasz => setHalak(valasz.data))
        .catch(hiba => console.log(hiba))
        .finally(() => setPending(false));
    }, []);

    return (
        <div className="container">
            {isPending ? (
                <ScaleLoader color="#007BFF" size={50} />
            ) : (
                <div className="grid-container">
                    {halak.map((hal, index) => (
                        <div className="card" key={index}>
                            <img src={hal.kep} alt={hal.nev} className="image" />
                            <h3 className="card-title">{hal.faj}</h3>
                            <p className="card-description">méret: {hal.meret_cm}</p>
                            <Link to={"/hal/" + hal.id} className="button">Részletek</Link>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
