import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { ScaleLoader } from "react-spinners";

export const TipusSingle = () => {
    const { halId } = useParams();
    const [hal, setTipus] = useState([]);
    const [isPending, setPending] = useState(false);

    useEffect(() => {
        const fetchData = async() => {
            setPending(true);
            try {
                const valasz = await axios.get(`https://localhost:5001/api/Halak/${halId}`)
                setTipus(valasz.data);
            }
            catch (hiba) {
                console.log(hiba);
            }
            finally {
                setPending(false);
            };    
    };
    fetchData();
    }, [halId]);

    return (
        <div className="single-container">
            {isPending || !hal ? (
                <ScaleLoader color="#7D3C98" size={50} />
            ) : (
                <div className="single-card">
                    <h3 className="single-title">{hal.nev}</h3>
                    <img src={hal.kep} alt={hal.nev} className="single-image" />
                    <p className="single-description">{hal.faj}</p>
                    <p className="single-description">{hal.meret_cm}</p>
                    <Link to="/" className="back-button">Vissza</Link>
                </div>
            )}
        </div>
    );
}
