import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { ScaleLoader } from "react-spinners";
import "./style.css";

export const HalakList = () => {
    const [halak, setHalak] = useState([]);
    const [isPending, setPending] = useState(false);

    useEffect(() => {
        fetchHalak();
    }, []);

    const fetchHalak = () => {
        setPending(true);
        axios.get("https://localhost:7067/api/Halak")
            .then(response => {
                if (Array.isArray(response.data)) {
                    const halakWithImages = response.data.map(hal => ({
                        ...hal,
                        kep: hal.kep ? `data:image/png;base64,${hal.kep}` : null,
                    }));
                    setHalak(halakWithImages);
                } else {
                    console.error("Hibás válaszformátum:", response.data);
                }
            })
            .catch(error => console.error("Hiba történt:", error))
            .finally(() => setPending(false));
    };

    const deleteHal = (id) => {
        if (window.confirm("Biztosan törlöd ezt a halat?")) {
            axios.delete(`https://localhost:7067/api/Halak/${id}`)
                .then(() => {
                    setHalak(halak.filter(hal => hal.id !== id));
                })
                .catch(error => console.error("Hiba történt a törlésnél:", error));
        }
    };

    return (
        <div className="container">
            {isPending ? (
                <ScaleLoader color="#007BFF" size={50} />
            ) : (
                <div className="grid-container">
                    {halak.map((hal) => (
                        <div className="card" key={hal.id}>
                            {hal.kep ? (
                                <img src={hal.kep} alt={hal.nev} className="image" />
                            ) : (
                                <p>Nincs kép</p>
                            )}
                            <h3 className="card-title">{hal.faj}</h3>
                            <p className="card-description">Méret: {hal.meretCm} cm</p>
                            <Link to={`/halak/${hal.id}`} className="btn btn-primary">Részletek</Link>
                            <Link to={`/mod-hal/${hal.id}`} className="btn btn-warning">
                                <i className="bi bi-pencil-square fs-3"></i>
                            </Link>
                            &nbsp;&nbsp;
                            <button onClick={() => deleteHal(hal.id)} className="btn btn-danger">
                                <i className="bi bi-trash2 fs-3"></i>
                            </button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};
