import React, { useEffect, useState } from 'react';
import axios from 'axios';

const HalakList = () => {
  const [halak, setHalak] = useState([]);

  useEffect(() => {
    axios.get('/api/halak')
      .then(response => setHalak(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1>Halak List</h1>
      <ul>
        {halak.map(hal => (
          <li key={hal.id}>
            <img src={`data:image/jpeg;base64,${hal.image}`} alt={hal.name} />
            <p>{hal.name}</p>
            <button onClick={() => window.location.href = `/hal/${hal.id}`}>View</button>
            <button onClick={() => axios.delete(`/api/halak/${hal.id}`).then(() => setHalak(halak.filter(h => h.id !== hal.id)))}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HalakList;
